namespace Infrastructure.Helpers;  public static class TenantHelper {     public static List<TenantConfig> Tenants = new();      public static TenantConfig? Get(int tenantId)     {         return Tenants?.FirstOrDefault(s => s.TenantId == tenantId);     }      public static string? GetTenantName(int tenantId)     {         return Tenants?.FirstOrDefault(s => s.TenantId == tenantId)?.Title;     } }